  <div id="inside_view">
    <div id="layer_div">
      <div id="layer_list">
        <div id="layer_title">Liste des couches</div>
        <div class="layer_line_descr" v-for="item in items">
          <div class="layer_name" :key="item.message" v-on:click="change_visibility(item.message, $event)">
            {{ item.message }}
          </div>
          <div class="layer_symbol"></div>
        </div>
      </div>
    </div>
    <div id="ol-container" class="map">
    </div>
  </div>

