//FUNCTION DE BASE
// ================================================================================
function style_vector (feature, resolution){
  let text_descr = feature.get('descr');
  return new Style({
    stroke: new Stroke({
      color: 'red',
      lineDash: [1],
      width: 1        
    }),
    text : new Text({
        text : text_descr,
        placement : 'Point',
        font : '12px Calibri, Courier New'
    })
  })
}

function create_vector(classi) {
  let Geojson =  new GeoJSON().readFeatures(classi['geojson_layer']);
  var class_layer = classi
  let vectorlay = new VectorLayer({
    source: new VectorSource({
      features: Geojson
    }),
    style : function(feature){
      let text_descr = feature.get('descr');
      return new Style({
        stroke: new Stroke({
          color: class_layer['color'],
          lineDash: [ class_layer['line']],
          width: class_layer['width_li']        
        }),
        fill : new Fill({
          color : class_layer['color_fill']
        }),
        text : new Text({
            text : text_descr,
            placement : 'Point',
            font : '20px Calibri, Courier New',
            fill: new Fill({
              color: class_layer['color']
            }),
            stroke: new Stroke({
              color: 'white',
              width: 3
            })
        })
      })
    },
    visible: true
  });
  return vectorlay
}

function  update_visibility(name_update, layer_name, layer, target){
  if(name_update == layer_name){
    if(layer.getVisible()){
      layer.setVisible(false)
      target.style.color = "gray";
    }else{
      layer.setVisible(true)
      target.style.color = "black";
    }  
  }
}


