class layer_json {
  constructor(color, color_fill, line, width_li, name_layer, geojson_layer) {
    this.color = color;
    this.color_fill = color_fill
    this.line = line;
    this.width_li = width_li;
    this.name_layer = name_layer;
    this.geojson_layer = geojson_layer;
  }
}

var liste_layer_json = [];

liste_layer_json.push(new layer_json('rgb(20, 0, 0, 1)','rgb(20, 0, 0, 0)', 1, 2, "Casier", json_casier))
liste_layer_json.push(new layer_json('blue','rgb(0, 0, 0, 0)', 10, 4, "Périmètre", json_perimetre))
liste_layer_json.push(new layer_json('red','rgb(250, 0, 0, 0.4)', 1, 4, "Secteur de cubage", json_secteur_cubage))

// Liste des layers avec nom
var vector_list = [];
vector_list.push(["Orthophoto", staticimage])
for (let value of liste_layer_json) {
  vector_list.push([value['name_layer'], create_vector(value)])
}

// Items for liste des couches
var item_list =[];
for (let value of vector_list) {
  item_list.push({message : value[0]})
}
//Création des paramètres de vue (zoom et centre)
// ================================================================================
const view_map = new View({
  center: [2521300, 1156000],
  projection: projection,
  zoom: 12,
  maxZoom: 16
});


export default {
  data:()=>{
    return {
      items: item_list
    }
  },
  mounted() {
    const map = new Map({
      target: "ol-container",
      view: view_map
    });

    map.addLayer(wmtsCadastre);
    for (let layer_i of vector_list) {
      map.addLayer(layer_i[1]);
    }
  },
  methods:{
    change_visibility(layer_name, event){
      for (let layer_i of vector_list) {
        update_visibility(layer_name, layer_i[0], layer_i[1], event.target)
      }
    }
  },
};
</script>

